namespace Bookstore.DataAccess.Context
{
    using Bookstore.Infrastructure.Context;
    using Microsoft.EntityFrameworkCore;
    using System.Threading.Tasks;
    using Entities;
    using System;

    /// <summary>
    /// Initialize context
    /// </summary>
    public class DBInitialize : IDBInitialize
    {
        private readonly BookstoreContext context;

        public DBInitialize(BookstoreContext context)
        {
            this.context = context;
        }

        /// <summary>
        /// Initialize function
        /// </summary>
        public async Task Initialize()
        {
            this.ApplyMigrations();
            await this.LoadData();
        }

        private void ApplyMigrations()
        {
            context.Database.Migrate();
        }

        private async Task LoadData()
        {
            await this.LoadAuthors();
            await this.LoadSeries();
            await this.LoadBooks();
        }

        private async Task LoadAuthors()
        {
            if (await context.Authors.AnyAsync())
            {
                context.Authors.Add(new Author() { Id = new Guid("92a4e08a-ae10-4095-ace2-1b87f89becd7"), Name = "Santiago", Lastname = "Posteguillo", Born = 1967 });
                context.Authors.Add(new Author() { Id = new Guid("d31bff9e-1f7f-42ad-af9e-1931149e0a89"), Name = "Simon", Lastname = "Scarrow", Born = 1962 });
                context.Authors.Add(new Author() { Id = new Guid("486bbb40-48b5-41a4-b5dc-63c163c93de7"), Name = "Joe", Lastname = "Abercrombie", Born = 1974 });
                context.Authors.Add(new Author() { Id = new Guid("6aa35f51-bc4f-41c1-be11-6b2168781d53"), Name = "Terry", Lastname = "Pratchett", Born = 1948 });
                context.Authors.Add(new Author() { Id = new Guid("cb1d95fc-465f-4ce5-b28a-247e22ecca84"), Name = "Patrick", Lastname = "Rothfuss", Born = 1973 });
                context.Authors.Add(new Author() { Id = new Guid("4c4334af-5cc8-41dc-84cc-b9646d5198f6"), Name = "Orson Scott", Lastname = "Card", Born = 1951 });
                context.Authors.Add(new Author() { Id = new Guid("960cb451-0d55-4f84-88e6-32a1b98b9724"), Name = "Dan", Lastname = "Simmons", Born = 1948 });
                context.Authors.Add(new Author() { Id = new Guid("8ddf1c84-7e53-4ec7-b06d-f321ff4b1d4f"), Name = "Massimiliano", Lastname = "Colombo", Born = 1966 });
                context.Authors.Add(new Author() { Id = new Guid("e801c5f4-af39-4b4a-bb7c-5e3bf8feb0bd"), Name = "Robert", Lastname = "Graves", Born = 1895 });
            }
        }

        private async Task LoadBooks()
        {
            if (await context.Books.AnyAsync())
            {
                context.Books.Add(new Book() { Id = new Guid("a8941ff8-7d1b-4e40-a289-b6824660012e"), SerieId = new Guid("72b1111e-e473-414c-9063-b3d59a12b1bb"), Year = 2006, Name = "Africanus, el hijo del cónsul", Description = "A finales del siglo III antes de Cristo, Roma se encontraba a punto de ser aniquilada por los ejércitos cartagineses al mando de Aníbal. Ese era su inexorable destino hasta que surgió un solo hombre, un joven oficial de las legiones, que transformó lo que debía ocurrir en lo que finalmente fue: la génesis de un imperio y una civilización secular en el tiempo y en la historia del mundo." });
                context.Books.Add(new Book() { Id = new Guid("4fb58b79-fa06-4d73-b1fa-2aa3f0066fec"), SerieId = new Guid("72b1111e-e473-414c-9063-b3d59a12b1bb"), Year = 2008, Name = "Las legiones malditas", Description = "Publio Cornelio Escipión, quien luego sería conocido por el sobrenombre de Africanus, se vio obligado por el senador todopoderoso Quinto Fabio Máximo, detractor de la familia de los Escipiones desde tiempo atrás, a aceptar la demencial tarea de liderar las legiones V y VI que permanecían desde hacía tiempo olvidadas en Sicilia. Éstas eran unas legiones malditas que habían perdido el sentido del deber, la disciplina y toda esperanza de regreso a su patria. Con esta maniobra, Quinto Fabio Máximo creía abocar al último Escipión de la estirpe a una muerte segura. Lo que Fabio Máximo no podía prever es que el joven Escipión y sus legiones malditas estaban llamados a cambiar el curso de la historia." });
                context.Books.Add(new Book() { Id = new Guid("1ec72ecf-b41c-49fe-b665-fcff7ccca37e"), SerieId = new Guid("72b1111e-e473-414c-9063-b3d59a12b1bb"), Year = 2009, Name = "La Traición de Roma", Description = "Con La traición de Roma se cierra la trilogía sobre Escipión y se nos muestra el ocaso de los diferentes personajes conocidos durante las dos novelas anteriores (tanto amigos como enemigos del Africano): Aníbal, Marco Porcio Catón y su aliado Graco, la esclava Netikerty, la prostituta Areté, el fiel Cayo Lelio, su familia, el dramaturgo Plauto. El ocaso de unos personajes en una Roma que resurge finalmente victoriosa pero que no duda en arrasar para ello incluso a sus héroes más destacados." });

                context.Books.Add(new Book() { Id = new Guid("d50b291a-092b-42f3-9118-6cffd7fe9d33"), Year = 2011, Name = "El estandarte púrpura", Description = "Un episodio crucial en la historia del imperio romano: La rebelión de la Reina Boudica. Britania, 60 d.C. Desde hace unos años la isla se halla bajo el yugo romano. Aunque las poblaciones locales están lejos de ser dominadas, es mayor el odio entre las tribus que aquel que sienten hacia Roma. Pero cuando el necio gobernador Suetonio humilla brutalmente a la reina de los icenos, Boudica, estalla la insurrección. Las legiones establecidas en la isla deben disponerse para una incierta batalla contra el más fuerte de los enemigos: la sed de libertad. Marco Quintinio Aquila, centurión de la Vigésima Legión, licenciado por heridas de guerra, se encuentra sin querer en medio de una rebelión. Sin embargo, en lugar de huir se enfrenta a su destino con corazón firme. Traicionado por la mujer amada, herido y perseguido, busca a su antiguo destacamento y consigue reunirse de nuevo con sus compañeros. Juntos se dirigirán hacia la llanura de Manduessedum, donde encontrarán un ejército enemigo diez veces superior en número. Son las tribus dirigidas por la reina Boudica, que los esperan para librar la gran batalla final. «Una gran historia épica, llena de acción y brillantemente documentada, sobre uno de los episodios más brutales de la historia de Roma y Britania; se trata de una novela histórica escrita con el rigor de los que conocen la profesión de las armas.» Tiempo «No es una novela histórica sin más. Hay acción, aventuras, romance, intriga y asesinatos.» Anika entre Libros «El autor crea una atmósfera magnífica. Las descripciones de la vida diaria, de las costumbres, de los vestidos, las comidas... son fabulosas.» La Historia en mis Libros «Consigue sumergirnos en el corazón y las almas de los legionarios que protagonizan la novela.» Revista Krítica «Trepidante historia de épica y honor.»" });
                context.Books.Add(new Book() { Id = new Guid("0ad015e2-40e3-4b74-9a1b-bb4809f50535"), Year = 2014, Name = "La legion de los inmortales", Description = "Año 55 a. C. Una flota de guerra avista una tierra desconocida que resulta estar poblada por feroces guerreros capaces de infundir temor incluso a los soldados de Julio César. Ante el pánico que se apodera de las tropas, un hombre se arroja a las gélidas aguas. Es Lucio Petrosidio, aquilífero de la Décima Legión. Como un solo hombre, detrás de su águila, la legión de los Inmortales acomete el asalto. Por César y por Roma, Lucio y sus compañeros, Máximo, Quinto y Valerio, se batirán sin tregua por conquistar Britania y para proteger a Gwynith, la esclava pelirroja que ha conquistado el corazón del aquilífero. Hasta un lugar llamado Atuatuca, donde acecha un destino de sangre... Año 35 a. C. Desde el puente de una nave, un hombre observa las costas de la gran isla ya próxima. A su lado, la espada corta de los legionarios; en la mente, los recuerdos de una epopeya de guerra y muerte en la que aletean los fantasmas de los compañeros caídos.Es para dar paz a esos fantasmas, y a su conciencia, que el viejo soldado regresa a Britania. Porque desde entonces hay una mujer a la espera de su hombre y hay una batalla iniciada veinte años antes que lo aguarda para concluir definitivamente." });
                context.Books.Add(new Book() { Id = new Guid("65b310b2-87da-42c4-8f36-fcb8f0061d4c"), Year = 1934, Name = "Yo, Claudio", Description = "Claudio, creyendo que alguien quiere acabar con su vida, decide escribir su biografía para dejar claro qué tipo de personas forman su familia y su entorno. Por ello, centra su narración en la dinastía Julio-Claudia y el Imperio Romano desde el asesinato de Julio César (44 a. C.) hasta el de su sobrino Calígula (41)." });

                context.Books.Add(new Book() { Id = new Guid("8d1fd5fc-6f99-40f1-a7a5-6c6277bb0b1f"), SerieId = new Guid("11571600-0500-4a4b-b29b-c1ccc5bca6b4"), Year = 1989, Name = "Hyperion", Description = "En el siglo XXVIII la humanidad se encuentra expandiéndose por la galaxia, después de haber abandonado el moribundo planeta Tierra original. La llamada Hegemonía del Hombre mantiene la paz en una sorprendente pero evolutivamente estanca y acomodada sociedad que forma la Red de Mundos durante los últimos tres siglos, asesorada por las misteriosas Inteligencias Artificiales, quienes adquirieron conciencia propia y se liberaron del control de hombre siglos atrás para abocarse a sus propios intereses. En el mundo de Hyperion, más allá de la Red de Mundos, se encuentran las Tumbas del Tiempo, artefactos enviados desde el futuro lejano que están a punto de abrirse y encierran un terrible secreto relacionado con una imposible criatura que sólo se comunica mediante la muerte, el Alcaudón, o Señor del Dolor, considerado por algunos como una deidad y por otros como avatar de la inminente expiación humana. Asimismo, unos seres exteriores conocidos como éxters, descendientes también de la vieja Tierra, han demostrado el mismo interés por este extraño mundo: por razones desconocidas, todos los grupos de poder del universo conocido desean tomar el control del planeta en el momento que se abrirán las Tumbas de Tiempo. En este contexto, siete personas se dirigen en Hyperion a una última peregrinación a su encuentro con el Alcaudón. Todos ellos, portadores de historias increíbles y temibles secretos, muestran al contar sus historias, pinceladas del complejo universo desarrollado por Simmons y de una sociedad abocada a una lucha por su destino, a la que tal vez puedan salvar." });
                context.Books.Add(new Book() { Id = new Guid("a86e196f-cb7f-4890-8426-5898f7c4870a"), SerieId = new Guid("11571600-0500-4a4b-b29b-c1ccc5bca6b4"), Year = 1990, Name = "La caída de Hyperion", Description = "En este volumen se da término al viaje realizado por los 7 peregrinos elegidos por la iglesia del Alcaudón y se otorga un mayor protagonismo a las inteligencias artificiales que residen en el tecnonúcleo. En este relato, se descubrirá donde se sitúa la parte física, el hardware, que sostiene a las IA's, y cuyo paradero humanos y exters desconocen. También se le dará cierre a las tramas de los peregrinos y se muestra cómo cada uno cumple su destino para salvar a la Humanidad. El libro arranca en Hyperion, donde los 7 peregrinos (seis, si no contamos el desaparecido Het Masteen) se dirigen a las Tumbas del Tiempo. También sigue los pasos de un cíbrido que es el 'gemelo' del cíbrido John Keats del que se enamoró Brawne Lamia. Este John Keats se hace pasar por un pintor y es contratado por Meina Gladstone, la FEM de la Hegemonía del Hombre, para que asista a las reuniones del centro de mando de FUERZA mientras coordinan la batalla por el sistema de Hyperion contra el Enjambre Éxter. Meina quiere cerca a Johny porque él tiene la capacidad de ver, a través de su gemelo que está en el bucle de Schroon en el cuerpo de Lamia, lo que los peregrinos hacen y sienten en tiempo real. Él ve las acciones de los peregrinos durante sus sueños y le reporta su estado a la FEM, pero pronto descubre que no son solo sueños y que él es más que un cíbrido o una IA." });

                context.Books.Add(new Book() { Id = new Guid("5d22d7ba-dd2d-46c1-a857-3807fc422fe4"), SerieId = new Guid("a66fec6a-d527-482e-a612-0301b4d6a6d0"), Year = 1985, Name = "El juego de Ender", Description = "En el año 2070 la humanidad está en guerra con una raza extraterrestre conocida como los insectores (su nombre proviene del parecido que tienen a los insectos). Tras fracasar en su Primera Invasión (de exploración) del sistema solar la cual está cerca de acabar con la humanidad, ésta establece una alianza mundial para hacer frente a la amenaza, con la creación de una unidad militar multinacional, la Flota Internacional (FI), otorgándose el gobierno de la tierra a la 'Hegemonía' dividiéndose el gobierno del planeta entre tres cuerpos: el Hegemon, el Polemarch, y el Strategos; los cuales pelean por el dominio durante la guerra. En este futuro la humanidad ha desarrollado el viaje interestelar, la comunicación más rápida que la luz (denominada ansible en las novelas de Ursula K. Le Guin), el control sobre la gravedad y otros mecanismos y armas (muchos descubiertos a partir de los restos de la Primera Invasión Insectora). La segunda invasión (que, a diferencia de la primera, era para colonizar) fue detenida en el último momento por el talento del estratega, Mazer Rackham. Pero han pasado décadas, y la humanidad se enfrenta a un futuro incierto esperando el exterminio por una posible tercera invasión por los extraterrestres." });
                context.Books.Add(new Book() { Id = new Guid("bfa6fbda-003b-4738-b559-122a981d226d"), SerieId = new Guid("a66fec6a-d527-482e-a612-0301b4d6a6d0"), Year = 1986, Name = "La voz de los muertos", Description = "La voz de los muertos transcurre 3000 años después de los eventos de El juego de Ender. Debido a efectos relativistas Ender, (Andrew Wiggin) permanece joven (apenas 35 años) tras haber viajado a velocidades cercanas a la de la luz por toda la galaxia, saltando de planeta en planeta, acompañado por su hermana Valentine. Ender busca un lugar en el que depositar a la Reina Colmena, el último superviviente de los insectores, la raza a la que Ender casi extermina en la primera novela de la serie El juego de Ender, motivo por el que tiene un profundo sentimiento de culpa. Su última parada ha sido Trondheim, donde Valentine se ha casado. Mientras, en el planeta Lusitania, una colonia reciente, se ha encontrado una nueva especie inteligente, los pequeninos, unos mamíferos también llamados despectivamente cerdis. Los xenólogos planetarios, Pipo y su hijo Libo, los estudian con fuertes restricciones del congreso, que impide hacer preguntas directas o mostrarles tecnología humana. La joven Novinha entra a trabajar como xenobióloga, puesto vacante desde la muerte de sus padres, Os Venerados, que murieron combatiendo la Descolada, un horrible virus que se bloquea con aditivos en la comida. Ender viaja allí como portavoz y se establece, revolucionando la vida de la colonia humana, pactando con los pequeninos y resucitando a la Reina Colmena. Pronto, las tres razas deberán trabajar juntas para evitar la destrucción del planeta, ya que el Congreso Estelar decide mandar a su Flota a atacar Lusitania para evitar la propagación de la descolada." });
                context.Books.Add(new Book() { Id = new Guid("0668a491-8cae-4ac4-962d-ae6d1a5e6a05"), SerieId = new Guid("a66fec6a-d527-482e-a612-0301b4d6a6d0"), Year = 1991, Name = "Ender el xenocida", Description = "En este episodio de la saga, se deberán resolver nuevos y viejos problemas. El Congreso Estelar ha enviado una flota dotada del Ingenio de Desintegración Molecular al planeta Lusitania para acabar con la amenaza de la descolada, que podría destruir rápidamente toda la vida de cualquier planeta a donde sea llevada. En el mundo de Sendero, en el que los dioses hablan a los agraciados, Han Fei-tzu encomienda a su hija Qing-jao que resuelva la desaparición de la flota lusitana, por orden del Consejo. Jane, en la nave en que Miro y Valentine se encuentran viajando hacia Lusitania, comprende que acabará por localizarla . Al llegar al planeta, descubre que los pequeninos han abrazado el cristianismo y que se está extendiendo una herejía según la cual la descolada es el espíritu santo que debe ser repartido por el universo. La Reina Colmena está construyendo una nave para los pequeninos, y el bosque de Guerrero, el árbol padre que fomenta la herejía, puede conseguir el control, por lo que Quim parte para intentar apaciguarle. Por otra parte, la familia de Novinha se encuentra envuelta en fuertes discusiones, ya que se ha descubierto que la descolada se comunica y podría ser inteligente. Cuando Quim llega al bosque de Guerrero, éste le apresa para intentar probar su dogma. Ender viaja para evitarlo, pero tras varios días Quim muere por la descolada. Esto enfurece a las otras tribus de pequeninos, que se preparan para la guerra, pero un tumulto formado por Grego comete una matanza en el bosque, interrumpida por los insectores. La situación se torna insostenible, a lo que se añade que Qing-jao revela al Congreso la existencia de Jane, que se dispone a eliminarla, mientras que su padre y su doncella Wang-mu descubren que los agraciados están en realidad afectados por una enfermedad diseñada por el congreso. Los intentos de crear una descolada aceptable para todos no resultan, pero tras la muerte de Plantador, que se sacrifica para demostrar que los Pequeninos son inteligentes aún sin descolada, Quara y Ela comienzan a trabajar juntas. Grego y Olhado, a partir de preguntas de Ender a la Reina Colmena, que le revela que las aiúas vienen de un exterior, desarrollan una forma de viajar más rápido que la luz. Desarrollan un receptáculo para poder crear en el exterior la recolada, una forma modificada de la descolada. La nave es pilotada por Jane, y Ender debe ir porque se ha descubierto que de él nace el aiúa de Jane. En el exterior, el lisiado Miro crea un cuerpo sano para sí mismo, mientras que Ender produce una copia de sus hermanos Peter y Valentine como permanecen en sus recuerdos de la juventud. Ambos parecen completamente independientes de Ender, y con los recuerdos de su creador que según su imagen deberían tener, y también pueden pilotar la nave. La recolada sustituye al viejo virus y Peter lleva una versión a Sendero diseñada para corregir el defecto genético que provocaba las supuestas visiones divinas. Después se lleva a Wang-mu con él para desbaratar el Congreso." });
                context.Books.Add(new Book() { Id = new Guid("0a5b868c-9481-4ee9-819b-5969629d7c64"), SerieId = new Guid("a66fec6a-d527-482e-a612-0301b4d6a6d0"), Year = 1996, Name = "Hijos de la mente", Description = "En este libro Ender y sus compañeros deberán ocuparse de varias cosas: evitar que la descolada acabe con ellos, detener a la flota Lusitania enviada por el Congreso Estelar para destruir el planeta y acabar con la amenaza del virus, o idear un modo de viajar a una velocidad mayor que la luz para escapar de ella y evitar que el Congreso Estelar destruya a Jane, además viajan al planeta que creó la descolada para descubrir sus intenciones y comunicarse con ellos. Ender es abandonado por su esposa Novinha quien se recluye en un convento. Aparecen dos nuevos personajes: Valentine quien es una copia rejuvenecida de su hermana y Peter copia fiel de su hermano muerto. Estos personajes se apoderan de la trama. Peter junto a Wan-Mu tratan de recuperar el poder que poseía el antiguo Hegemon y derribar al Congreso Estelar. Olhado y Greco descubren la manera de que Jane haga viajes intergalácticos en segundos y proponen una nueva forma de concebir el universo. La reina colmena y Humano se comunican continuamente y tratan de ayudar a Jane para cuando esta sea deconectada de los ansibles de la Confederación. La recién creada Valentine se une a Miro para encontrar nuevos mundos para salvar a todas las especies de Lusitania, además tratan de comunicarse con el planeta de la descolada junto a Quara, Miro y Ela. Ender pierde fuerzas y cae enfermo a causa de manterner vivos a los nuevos Peter y Valentine, pero la verdadera razón es que ya no está interesado en vivir la vida que tiene con Novinha, esta descubre en su pasado que tiene que superar sus traumas para dejar libre a Ender." });
                context.Books.Add(new Book() { Id = new Guid("1bdd9604-cd1e-49b4-91ad-7ebe8481bb0b"), SerieId = new Guid("a66fec6a-d527-482e-a612-0301b4d6a6d0"), Year = 2008, Name = "Ender en el exilio", Description = "Ocurre unos cuarenta años después de lo narrado en El juego de Ender y 2.650 antes de La voz de los muertos, y enlaza también con la serie posterior de La sombra de Ender, gracias a un uso inteligente de los efectos relativistas. Ender Wiggin, tras vencer a los insectores y dejar la Escuela de Batalla, parte a la búsqueda del planeta en el que se habían reunido las reinas colmenas de los insectores para intentar entender por qué se hallaban todas en un único planeta, lo que hacía a su especie extraordinariamente vulnerable.Ender se dirige a la colonia Shakespeare como gobernador, pero el comandante de la nave que lo lleva desea convertirse en el poder en la sombra detrás de Ender o, cuando menos, enviar a éste de nuevo al asteroide Eros (lugar donde se encontraba la ahora en desuso Escuela de Alto Mando)para quedarse con el poder. Ender, ahora de 13 años de edad, ve venir el golpe y lo previene, ya que sigue siendo un excepcional estratega. Al final Ender recala en la Colonia Ganges, fundada por Virlomi, antigua alumna de la escuela de batalla, y con la presencia en la colonia de Aquiles (hijo de Bean, LA SOMBRA DE ENDER, y su antiguo lugarteniente en la Escuela de Batalla en EL JUEGO DE ENDER). Tras veinticinco años de éxito, llega al fin la tan esperada continuación de EL JUEGO DE ENDER, una de las novelas más famosas de la moderna ciencia-ficción." });

                context.Books.Add(new Book() { Id = new Guid("4dbb5686-7197-4b4b-8f64-8e0a62cd3e94"), SerieId = new Guid("d3297d76-e4dc-43de-85d2-a1ff237ef437"), Year = 2007, Name = "El nombre del viento", Description = "'Me llamo Kvothe, que se pronuncia ‘cuouz’. Los nombres son importantes porque dicen mucho sobre la persona. He tenido más nombres de los que nadie merece. Los Adem me llaman Maedre. Que, según como se pronuncie, puede significar la Llama, el Trueno o el Árbol Partido. Mi primer mentor me llamaba E’lir porque yo era listo y lo sabía. Mi primera amante me llamaba Dulator porque le gustaba cómo sonaba. Me han llamado Kvothe el Sin Sangre, Kvothe el Arcano y Kvothe el Asesino de Reyes. Todos esos nombres me los he ganado. Los he comprado y he pagado por ellos. Pero crecí siendo Kvothe. Una vez mi padre me dijo que significaba ‘saber’. He robado princesas a reyes agónicos. Incendié la ciudad de Trebon. He pasado la noche con Felurian y he despertado vivo y cuerdo. Me expulsaron de la Universidad a una edad a la que a la mayoría todavía no los dejan entrar. He recorrido de noche caminos de los que otros no se atreven a hablar ni siquiera de día. He hablado con dioses, he amado a mujeres y he escrito canciones que hacen llorar a los bardos. Quizá hayas oído hablar de mí.' Así empieza una historia extraordinaria. Músico, mendigo, ladrón, estudiante, mago, héroe y asesino: esta es la leyenda que se ha forjado alrededor de la figura de Kvothe. Ahora, por primera vez, él va a relatar la verdad sobre sí mismo. Y para hacerlo bien, deberá empezar por el principio: su infancia en una troupe de artistas itinerantes, los años malviviendo como un ladronzuelo en las calles de una gran ciudad, y su llegada a una universidad donde espera aprender una magia de la que se habla en las historias… Atípica, profunda y sincera, El nombre del viento es una novela de aventuras, de historias dentro de otras historias, de misterio, de amistad, de amor y de superación, escrita con la mano de un poeta y que ha deslumbrado -por su originalidad y la maestría con que está narrada- a todos los que la han leído." });
                context.Books.Add(new Book() { Id = new Guid("e48ef14e-ff6d-4dd2-a760-214de26b841c"), SerieId = new Guid("d3297d76-e4dc-43de-85d2-a1ff237ef437"), Year = 2011, Name = "El temor de un hombre sabio", Description = "El hombre había desaparecido. El mito no. Músico, mendigo, ladrón, estudiante, mago, trotamundos, héroe y asesino, Kvothe había borrado su rastro. Y ni siquiera ahora que le han encontrado, ni siquiera ahora que las tinieblas invaden los rincones del mundo, está dispuesto a regresar. Pero su historia prosigue, la aventura continúa, y Kvothe seguirá contándola para revelar la verdad tras la leyenda. Todos los lectores que se dejaron cautivar por la magia de El nombre del viento ansiaban leer esta novela: El temor de un hombre sabio, el segundo día de la historia de Kvothe. La espera ha terminado, y ha valido la pena. Comienza el segundo día, y Kvothe –héroe y villano de miles de historias que circulan entre la gente- retoma su narración en el punto donde la dejó al final de El nombre del viento: en la Universidad. De allí deberá partir en busca del nombre del viento y de fortuna, en pos de esas historias escondidas en libros polvorientos o que se relatan junto a una hoguera en el camino o en una taberna, y que cada vez le acercan más al día en que podrá vengarse de los Chandrian. Una formidable narración que vuela a lo más alto de la literatura fantástica para atrapar tanto a los exigentes aficionados al género como a los lectores que simplemente busquen perderse en una historia escrita con maestría, que sorprende y emociona." });
                context.Books.Add(new Book() { Id = new Guid("22d9616e-c3d0-4930-98a5-0dd4b29ab100"), SerieId = new Guid("d3297d76-e4dc-43de-85d2-a1ff237ef437"), Year = 3000, Name = "La puerta de piedra", Description = "" });
                context.Books.Add(new Book() { Id = new Guid("d87c0c7c-f5e0-4b51-9c69-4a8d4809cb0b"), SerieId = new Guid("d3297d76-e4dc-43de-85d2-a1ff237ef437"), Year = 2014, Name = "La música del silencio", Description = "novela corta protagonizada por Auri, no se trata de una continuación de Crónica del asesino de reyes, simplemente una historia paralela que no continúa tras el final de El temor de un hombre sabio." });

                context.Books.Add(new Book() { Id = new Guid("5b238bf5-81ae-4cc0-b110-1616bfd40ec2"), SerieId = new Guid("0c95557c-5bba-428d-96ac-04ba63c0db4f"), Year = 2006, Name = "La voz de las espadas", Description = "El inquisidor Glokta, convertido en un cínico tullido tras su paso por las cárceles de los enemigos de la Unión, es ahora a su vez un eficaz torturador capaz de extraer información de cualquiera. A su vez, el capitán Jezal dan Luthar no ha hecho otra cosa en su vida que desplumar a sus amigos jugando a las cartas y soñar con la gloria de vencer en el Certamen de esgrima. Pero se está fraguando una guerra, y en los campos de batalla del Norte la lucha se rige por normas mucho más sangrientas… Logen Nuevededos, infame bárbaro de pasado sangriento, acaba de perder a sus amigos y está decidido a abandonar sus tierras y dirigirse al Sur, pero los espíritus le advierten que le busca un Mago de los Viejos Tiempos… Sus historias se entrelazan en una fantasía negra repleta de acción y personajes memorables." });
                context.Books.Add(new Book() { Id = new Guid("35da7ca8-1344-418c-b732-7a47890409be"), SerieId = new Guid("0c95557c-5bba-428d-96ac-04ba63c0db4f"), Year = 2007, Name = "Antes de que los cuelguen", Description = "El Superior Glokta tiene la misión de defender una ciudad sitiada por el ejército gurko y minada por la traición, además de descubrir qué ocurrió con su sucesor. Por su parte, los hombres del Norte han cruzado la frontera y han entrado a sangre y fuego en el territorio de la Unión; para detenerlos no bastará con el ejército del Rey. A su vez, Bayaz, el Primero de los Magos, conduce a un heterogéneo grupo de aventureros en una peligrosa misión por las ruinas del pasado." });
                context.Books.Add(new Book() { Id = new Guid("972fa3a9-ee5c-47c2-84b7-d17a968aa666"), SerieId = new Guid("0c95557c-5bba-428d-96ac-04ba63c0db4f"), Year = 2008, Name = "El último argumento de los reyes", Description = "El rey de los hombres del Norte se mantiene, y solo hay un guerrero que le pueda detener: su viejo amigo y su enemigo más antiguo; ha llegado la hora de que el Sanguinario vuelva a casa... Glokta está librando una lucha secreta en la que nadie está seguro y nadie es de fiar. Y como sus días de guerrero están lejos, utiliza las armas que le quedan: chantaje, tortura... Jezal dan Luthar ha decidido que la gloria es demasiado dolorosa y prefiere una vida sencilla con la mujer a la que ama. Pero el amor también puede ser doloroso y la gloria tiene la desagradable costumbre de aferrarse a un hombre cuando menos la desea... El Rey de la Unión ha muerto, los campesinos se rebelan y los nobles luchan por su corona. Sólo el Primero de los Magos tiene un plan para salvar el mundo, pero esta vez hay riesgos. Y no hay un riesgo más terrible que romper la Primera Ley... “El último argumento de los reyes” es el volumen que cierra la impresionante trilogía de una voz que ya es imprescindible en la fantasía moderna." });

                context.Books.Add(new Book() { Id = new Guid("4a4ffe1e-89f9-432e-9be6-35453a4a139a"), Year = 2012, Name = "La mejor venganza", Description = "La guerra puede ser un infierno, pero para Monza Murcatto, la Serpiente de Talins, a sueldo del duque Orso, también es una forma excelente de hacer dinero. Sus victorias la han hecho muy popular —quizá demasiado para el gusto de Orso. Traicionada y dada por muerta, la recompensa de Murcatto es un cuerpo desfigurado y una sed de venganza que no se detendrá ante nada.  Sus aliados son el borracho menos fiable de Styria, el envenenador más traicionero de Styria, un asesino en serie obsesionado con los números y un bárbaro que quiere enderezar su vida… Entre sus enemigos están los más poderosos del país, y eso antes de que el Orso envíe al hombre más peligroso para acabar con ella… " });


                context.Books.Add(new Book() { Id = new Guid("3794daee -c339-468c-9c8a-2d3479cc63b"), SerieId = new Guid("582710ea-95f2-4ce4-b33d-9a347bb86ea3"), Year = 2000, Name = "El águila del Imperio", Description = "En el prólogo de El águila del Imperio se narra un hecho ocurrido durante la primera invasión romana de Britania, por el año 55 a. C., en el que un grupo de legionarios, entre los que se encuentra su comandante Julio César, entierran un cofre con el dinero de las pagas en una ciénaga, debido a que se ha quedado atrapada en el barro; después de hacer un rudimentario mapa con la localización del tesoro, huyen de las bandas de guerreros britanos que los acosan hacia el último barco que zarpa de la orilla. Luego la acción se traslada al cuartel de invierno de la Legio II Augusta a la orilla del Rin, en el año 43 d. C., al que llega una nueva tanda de reclutas en la que se encuentra el liberto Quinto Licino Cato con una carta del emperador Claudio para el legado de la legión, Tito Flavio Vespasiano, en el que insta a ascender a optio al recluta Cato, lo que produce un rechazo inmediato hacia él por parte de sus compañeros, incluido su centurión Lucio Cornelio Macro, aunque luego Cato demuestra valor y coraje al salvar al centurión Macro de un grupo de germanos, durante una emboscada que sufre una cohorte mandada por el tribuno superior Aulo Vitelio en un pueblo a la otra orilla del Rin, en la que finalmente vencen gracias a la intervención del resto de la legión." });
                context.Books.Add(new Book() { Id = new Guid("12a98356-33ad-490a-8a11-693fe20db636"), SerieId = new Guid("582710ea-95f2-4ce4-b33d-9a347bb86ea3"), Year = 2001, Name = "Roma Vincit!", Description = "Los legionarios Cato (optio) y Macro (centurión), al mando de la sexta centuria de la cuarta cohorte de la Legio II Augusta, continúan en la campaña de conquista de Britania formando parte del ejército del general Aulo Plautio. Una vez cruzado el río Támesis (tras las batallas del río Medway y del río Támesis) el ejército romano debe detenerse para esperar la llegada del emperador Claudio que asumirá el mando para consumar la conquista en la batalla decisiva por la capital de los catuvellani, Camuloduno. A la dureza de la resistencia britana se une el descubrimiento que desde Roma alguien ha estado abasteciendo de armamento al enemigo. Las sospechas recaen sobre una organización secreta llamada los Libertadores que pretenden derrocar al emperador para reinstaurar la República. Para ello, pretenden atentar contra Claudio cuando éste llegue a Britania. En realidad no son los Libertadores los que están tras estas traiciones sino el tribuno superior de la segunda legión, Vitelio, que conspira para preparar su futuro político en Roma. Cato y Macro conseguirán impedir el atentado al emperador pero las circunstancias harán que el verdadero conspirador, Vitelio, quede como un héroe y regrese a Roma junto al emperador como su salvador. Finalmente, la Legio II Augusta al mando del legado Vespasiano, permanece en Britania con nuevas misiones que realizar." });
                context.Books.Add(new Book() { Id = new Guid("d472f7a5-1aab-4477-8660-cfb2a97c39f0"), SerieId = new Guid("582710ea-95f2-4ce4-b33d-9a347bb86ea3"), Year = 2002, Name = "Las garras del águila", Description = "La Legio II Augusta, bajo el mando del legado Vespasiano, continúa en plena campaña de conquista de Britania integrada en el ejército comandado por el general Aulo Plautio. La principal resistencia que encuentran las legiones romanas viene de parte de la tribu de los durotriges, que están dirigidos por una casta de peligrosos druidas conocidos como de la Luna Oscura. Los planes romanos de conquista se verán dificultados por el secuestro por parte de los druidas de la familia del general Plautio. Si el general quiere volver a ver a su familia con vida deberá liberar a los líderes druidas capturados en distintas escaramuzas. Los legionarios Cato (optio) y Macro (centurión) reciben la misión suicida de adentrarse en territorio enemigo para intentar localizar y liberar a la familia del general. Para ello contarán con la ayuda de dos britanos aliados de los romanos, dos miembros de la tribu de los icenos, el principe Prasutago y su prometida Boadicea (antigua amante de Macro)." });
                context.Books.Add(new Book() { Id = new Guid("97ee8528-e175-499d-bfdf-6d34113c9c87"), SerieId = new Guid("582710ea-95f2-4ce4-b33d-9a347bb86ea3"), Year = 2003, Name = "Los Lobos del águila", Description = "Las legiones del general Aulo Plautio continúan su campaña de conquista de Britania ante la resistencia del líder britano Carataco y una coalición de tribus que lo apoyan. La Legio II Augusta, bajo el mando del legado Vespasiano, lleva a cabo su misión separada del cuerpo del ejército en territorio de sus aliados atrébates. Uno de los peligros a que se enfrentan las legiones, es el continuo ataque que sufren sus líneas de abastecimiento por parte de los durotriges, aliados de Carataco, por lo que los centuriones Cato y Macro (convalecientes de sus heridas) reciben la misión de entrenar dos cohortes auxiliares de atrébates para proteger la retaguardia romana. Las dos cohortes, los Lobos y los Jabalíes, deberán también proporcionar protección al rey atrébate Verica y a su capital, Calleva. Pero los dos protagonistas deberán también hacer frente a las conjuras internas entre los propios atrébates, los cuales están divididos entre los que desean mantener la alianza con Roma y los que creen que deberían unirse a Carataco y luchar por una Britania libre de romanos." });
                context.Books.Add(new Book() { Id = new Guid("d9a6c7c6-8c6d-4cf6-b942-10c3667c12dc"), SerieId = new Guid("582710ea-95f2-4ce4-b33d-9a347bb86ea3"), Year = 2004, Name = "El águila abandona Britania", Description = "La campaña romana por la conquista de Britania continúa y los centuriones Macro y Cato han recibido respectivamente el mando de la tercera y sexta centurias de la Tercera cohorte en la Legio II Augusta del legado Vespasiano. El líder britano Carataco continúa siendo el principal quebradero de cabeza de las legiones romanas y el general Aulo Plautio elabora un plan para acabar definitivamente con su resistencia; pero un grave error del centurión superior de la Tercera cohorte, Maximio, permite que Carataco y parte de su ejército escape de la trampa y se refugien en una zona pantanosa inaccesible para las legiones. Este grave error del centurión superior y su difícil personalidad pondrá a los dos protagonistas en sucesivos peligros, acabando Cato condenado a muerte (y fugado) y Macro arrestado por insubordinación. Sólo la determinación y valentía de los dos centuriones les permitirá salir con éxito de tan complicada situación, aunque acabará cayendo sobre ellos la sospecha del asesinato del centurión Maximio. Así pues, deberán abandonar Britania junto con el legado Vespasiano que acaba su mandato al frente de la Segunda legión." });
                context.Books.Add(new Book() { Id = new Guid("b69f736f-5302-42a4-81b2-27f005b095e4"), SerieId = new Guid("582710ea-95f2-4ce4-b33d-9a347bb86ea3"), Year = 2005, Name = "La profecía del águila", Description = "Los centuriones Macro y Cato, permanecen en Roma a la espera de que se les asigne un nuevo destino en las legiones ya que todavía está pendiente de resolución la pena de muerte que pesa sobre Cato y las sospechas contra ambos por el asesinato del centurión Maximio en Britania. El secretario imperial Narciso requiere sus servicios para llevar a cabo una misión secreta al servicio del emperador Claudio. Si la resuelven satisfactoriamente podrán olvidar sus problemas con la justicia. Los dos protagonistas deberán unirse a la flota romana de Rávena como centuriones de la armada para llevar a cabo una campaña contra los piratas que asuelan el Adriático. Macro y Cato deberán recuperar unos documentos que el líder de los piratas, Telémaco, robó a uno de los agentes de Narciso. Unos documentos de gran importancia tras los que también van los enemigos del emperador, la sociedad secreta conocida como Los Libertadores que pretenden reinstaurar la República. Para llevar a cabo la misión, Cato y Macro contarán con la ayuda del nuevo prefecto de la flota, Vitelio, el que fuera tribuno superior de la Legio II Augusta y enemigo de los dos centuriones. A la amenaza de los piratas se une la del propio Vitelio y la de un traidor que mantiene informados a los piratas de todos los movimientos de la flota romana. Vitelio no dudará en intentar apoderarse de los documentos para su beneficio personal, pero sus errores en el inicio de la campaña contra los piratas propiciarán la llegada de Vespasiano (quien fuera legado de la Segunda legión) como nuevo prefecto de la flota." });
                context.Books.Add(new Book() { Id = new Guid("689db381-ed29-4686-abe6-bb8bce234dde"), SerieId = new Guid("582710ea-95f2-4ce4-b33d-9a347bb86ea3"), Year = 2006, Name = "El águila en el desierto", Description = "Los centuriones Cato y Macro reciben nuevas órdenes del secretario imperial Narciso: deberán desplazarse a la frontera oriental del imperio, a la provincia de Judea, donde Macro tomará el mando como prefecto de la Segunda Cohorte auxiliar iliria en el fuerte de Bushir. Aunque la verdadera misión de los dos legionarios consistirá en averiguar hasta que punto se encuentran Los Libertadores (sociedad secreta contraria al emperador Claudio) detrás de las sublevaciones judías que amenazan con desestabilizar la zona; y el grado de implicación del gobernador de Siria, Casio Longino, en tales hechos. Una vez en Judea los dos centuriones serán testigos de la hostilidad del territorio; del fanatismo religioso de los nativos y de la corrupción generalizada entre los oficiales romanos, especialmente del centurión Póstumo (uno de los hombres de confianza de Longino). Así como del apoyo del Imperio Parto a los rebeldes judíos contra los romanos. Pero también entrarán en contacto con un guía autóctono, Simeon, y con los miembros de una nueva religión: los seguidores de Yehoshua, un líder judío crucificado años antes por los romanos. Hombres y mujeres comandados por Miriam, la madre de Yehoshua, que abogan por el perdón y la paz entre los diferentes pueblos." });
                context.Books.Add(new Book() { Id = new Guid("949570e6-fd69-4ec0-9be6-b677952cc1a9"), SerieId = new Guid("582710ea-95f2-4ce4-b33d-9a347bb86ea3"), Year = 2007, Name = "Centurión", Description = "El nuevo prefecto Macro y el centurión Cato, al mando de la Segunda Cohorte auxiliar iliria, han sido trasladados desde Judea a Antioquía (Siria) donde permanecen acuartelados. Su investigación por orden del secretario imperial Narciso sobre el gobernador de Siria, Casio Longino, ha quedado en un segundo plano por la falta de pruebas y por la inminente guerra contra Partia. De la vecina Palmira llegan noticias de una revuelta contra el rey Vabathus (aliado de Roma) encabezada por su hijo menor el príncipe Artaxas (partidario de los partos). El gobernador Longino decide enviar una avanzadilla de dos cohortes para ayudar a sus aliados mientras él se traslada con el grueso del ejército: Macro tomará el mando como centurión superior de la Cuarta cohorte de la Décima legión y Cato es nombrado prefecto interino de la Segunda cohorte auxiliar iliria. En su camino hacia la ciudad de Palmira las dos cohortes romanas se aliarán con el hijo mediano del rey, el príncipe Balthus y su caballería. Una vez en Palmira, las dos cohortes romanas y sus aliados palmireños quedan sitiados en la ciudadela interior rodeados de los rebeldes a la espera de ver cuál de los dos ejércitos llegan antes a la ciudad: el romano o el parto. En medio de los ataques de los rebeldes Cato encontrará el amor en Julia, la hija del embajador romano. La llegada de Casio Longino al frente de las legiones Décima y Tercera salvará la situación en la ciudadela. Pero la tranquilidad durará poco pues pronto hará su aparición el grueso del ejército parto." });
                context.Books.Add(new Book() { Id = new Guid("4f98d3bc-3625-46bc-a1ad-fce29283e19e"), SerieId = new Guid("582710ea-95f2-4ce4-b33d-9a347bb86ea3"), Year = 2009, Name = "El gladiador", Description = "Los centuriones Macro y Cato junto al senador Sempronio y su hija Julia (prometida de Cato) regresan de Palmira hacia Roma cuando un terremoto (que genera una ola gigante) provoca el naufragio de su barco en la isla de Creta. El terremoto ha sumido en el caos la provincia romana y, ante la ausencia de autoridad, el senador Sempronio decide tomar el mando como gobernador interino para restaurar el orden y socorrer a los habitantes. Pero un peligro mayor amenaza toda la provincia: la rebelión de un ejército de esclavos liderados por el gladiador Áyax, un viejo conocido de los dos centuriones por ser el hijo del jefe de los piratas a los que se enfrentaron tiempo atrás (en La profecía del águila). Macro y Cato participaron en la derrota y crucifixión de su padre y en la venta de Áyax como esclavo por lo que éste ve una doble oportunidad de venganza: contra Roma y contra los que mataron a su padre. Mientras Sempronio y Macro se quedan para resistir en Gortina, capital de la isla, Cato es nombrado tribuno por el senador y recibe la misión de navegar hacia Egipto para solicitar refuerzos al legado Petronio que está al mando de las legiones Vigésimo segunda y Tercera Cirenaica. A su regreso a la isla con los refuerzos Cato descubre que Áyax ha conseguido capturar a Julia y a Macro y pretende utilizarlos como rehenes. Cato, como tribuno al mando de las tropas romanas en la isla, deberá utilizar toda su astucia para derrotar a los rebeldes y liberar a sus amigos. Finalmente conseguirá liberarlos y acabará con la rebelión de esclavos, pero Áyax escapa en un barco arrebatado a los romanos. El senador Sempronio encarga a los dos protagonistas perseguir y capturar al gladiador poniendo a Cato al frente de la misión como nuevo prefecto. Por primera vez Cato ostentará un rango superior a su viejo amigo Macro." });
                context.Books.Add(new Book() { Id = new Guid("2158e986-67ec-49cb-8487-71cfcf544d07"), SerieId = new Guid("582710ea-95f2-4ce4-b33d-9a347bb86ea3"), Year = 2010, Name = "La Legión", Description = "El prefecto Cato y el centurión Macro, al mando de varios buques de guerra romanos, continúan con la misión de capturar al ex gladiador Áyax que asola las costas egipcias con su banda de rebeldes. Tras varios sangrientos encontronazos, las tropas romanas consiguen destruir gran parte de las fuerzas de Áyax, pero éste consigue escapar en el último momento ante la desesperación de los dos amigos. Cato y Macro desean continuar con la persecución de su viejo enemigo, pero el gobernador de Egipto, Petronio, les obliga a incorporarse como oficiales a la Vigésimosegunda legión, que está preparándose para hacer frente a una invasión por parte del ejército nubio al mando del príncipe Talmis. Al llegar a su nuevo destino, Cato como Tribuno Superior y Macro como Primer Centurión, descubrirán que la Vigesimosegunda lleva demasiado tiempo sin entrar en combate por lo que se ha convertido en una legión desentrenada y poco profesional. Los dos veteranos oficiales deberán utilizar todos sus recursos, su dilatada experiencia y sus conocimientos militares para devolver a las tropas romanas el nivel militar que les corresponde lidiando además con un superior, el legado Aurelio, que no está a la altura de su rango. La misión se complicará más si cabe cuando Cato y Macro descubran que Áyax y sus hombres se han unido al ejército nubio y cuando, tras la muerte de Aurelio, Cato tenga que ponerse al mando del ejército romano." });
                context.Books.Add(new Book() { Id = new Guid("54cbdc1e-acbf-4eae-a1e0-02709b86395b"), SerieId = new Guid("582710ea-95f2-4ce4-b33d-9a347bb86ea3"), Year = 2011, Name = "Pretoriano", Description = "Tras su estancia en Egipto, los centuriones Cato y Macro regresan a Roma con una nueva misión del secretario imperial Narciso: deberán unirse a las filas de la Guardia Pretoriana y descubrir hasta que punto la organización secreta conocida como Los Libertadores está infiltrada en su estructura. Si Cato quiere recuperar su cargo de prefecto accediendo así a la clase ecuestre (pudiendo optar a desposarse con su amada Julia) y Macro volver a las legiones, deberán llevar a cabo la misión encomendada y averiguar quienes son los miembros de la Guardia que conspiran contra el emperador Claudio. Cato y Macro, con identidades falsas, son destinados como soldados rasos a la centuria encargada de la protección del palacio y de la familia imperial, por lo que, mientras llevan a cabo su misión, serán testigos del pulso de poder que está fraguándose entre los dos posibles sucesores de Claudio: su hijo legítimo Británico y Nerón, el hijo de su segunda mujer Agripina. Los dos veteranos legionarios deberán cubrirse las espaldas y poner en práctica toda su astucia y recursos para sobrevivir a las intrigas políticas palaciegas, descubriendo que pueden llegar a ser más peligrosas que cualquier escaramuza contra los bárbaros de las fronteras de Roma." });
                context.Books.Add(new Book() { Id = new Guid("f58e8e88-878d-46e7-9ad6-65d60d1663f6"), SerieId = new Guid("582710ea-95f2-4ce4-b33d-9a347bb86ea3"), Year = 2013, Name = "Cuervos sangrientos", Description = "Han pasado ocho años desde que los romanos iniciaron la conquista de Britania y siete desde que el emperador Claudio diera por sometida la región, pero la realidad es que a pesar de los intentos del gobernador Ostorio Escápula por establecer acuerdos de paz, Carataco, rey de los catuvellaunos, continúa su incansable lucha contra los invasores uniendo bajo su mando a todas las tribus que no desean someterse a Roma. En esta delicada situación, regresan a Britania (ver las cinco primeras novelas de la serie) el nuevo prefecto Cato (recién casado con su amada Julia) y el centurión superior Macro destinados a la Decimocuarta Legión, donde Cato debe tomar el mando de la Segunda Cohorte auxiliar Tracia de caballería y Macro ponerse al frente de la Cuarta Cohorte de la legión. Mientras el gobernador prepara su ejército para la guerra, Cato y Macro llegan a su nuevo destino: el fuerte Bruccio, situado en un aislado valle en pleno territorio enemigo. Allí descubrirán que el comandante en funciones del fuerte: el centurión Querto y su Cohorte auxiliar Tracia se han ganado una sanguinaria reputación entre los nativos, los siluros, que se refieren a los tracios como: “Cuervos sangrientos” por los terroríficos métodos que utilizan para someter cualquier resistencia a Roma. Cato y Macro deberán preparar a sus hombres para la inminente guerra contra Carataco, y al tiempo cubrirse las espaldas, pues el centurión Querto no está dispuesto a ceder fácilmente el poder que tiene sobre el valle." });
                context.Books.Add(new Book() { Id = new Guid("26577528-ed94-4a69-bdf1-57b8e33027b9"), SerieId = new Guid("582710ea-95f2-4ce4-b33d-9a347bb86ea3"), Year = 2014, Name = "Hermanos de sangre", Description = "El prefecto Cato, al mando de la Segunda Cohorte auxiliar Tracia de caballería, y el centurión superior Macro, al mando de la Cuarta Cohorte, integrados en la Decimocuarta Legión continúan en la campaña militar de Britania bajo el mando del gobernador Ostorio Escápula. El objetivo del ejército romano es someter definitivamente a Carataco, rey de los catuvellaunos, que continúa su incansable lucha contra los invasores uniendo bajo su mando a todas las tribus que no desean someterse a Roma. Los dos veteranos legionarios verán como su existencia se complica cuando descubren que las intrigas políticas de Roma, que creían haber dejado atrás, les han perseguido hasta aquella lejana provincia para poner en peligro tanto la conquista de Britania como sus propias vidas. Y es que en Roma, Británico y Nerón, hijos del emperador Claudio, están enfrentados por convertirse en el sucesor de su padre. Sus respectivos consejeros, Narciso y Palas, protagonizan una encarnizada guerra secreta con el objetivo de socavar el poder de sus contrarios. El fracaso de la campaña en Britania sería también el fracaso de Narciso, por lo que Palas ha enviado a uno de sus agentes para sabotearla y de paso asesinar a los dos legionarios, que sirvieron a Narciso en distintas misiones. De nuevo, Cato y Macro deberán cubrirse mutuamente las espaldas y evitar que el saboteador tenga éxito en su misión, al tiempo que deben cumplir con su deber como soldados de Roma buscando la derrota definitiva de Carataco." });
                context.Books.Add(new Book() { Id = new Guid("03e197cf-6ed0-4229-87d3-ede8664fb058"), SerieId = new Guid("582710ea-95f2-4ce4-b33d-9a347bb86ea3"), Year = 2015, Name = "Britania", Description = "En el año 52 d. C, las tribus occidentales de la Britania, inspiradas por el gran odio de los druidas hacia los romanos, se preparan para atacar. ¿Seran capaces de enfrentarse a la disciplina y el coraje de los legionario?" });
                context.Books.Add(new Book() { Id = new Guid("81278798-b68a-43a6-a40a-7fc88e03372f"), SerieId = new Guid("582710ea-95f2-4ce4-b33d-9a347bb86ea3"), Year = 2016, Name = "Invictus", Description = "The Sunday Times bestesller. INVICTUS is a gripping, authentic and exciting novel of the Roman army from bestselling author Simon Scarrow. Not to be missed by readers of Conn Iggulden and Bernard Cornwell. It is AD 54. The soldiers of the Roman army patrol a growing Empire, from the Mediterranean to the North Sea, from the Atlantic to the banks of the Nile. Rome brutally enforces its rule, and its legions are the most efficient and aggressive fighting force in the world. Two battle-scarred veterans of this army, Prefect Cato and Centurion Macro, have survived years of campaigning in Britannia and have been recalled to Rome. Their time in the teeming, dangerously political city is short, and soon they are travelling with the Praetorian Guard to Spain, a restless colony where simmering tension in the face of Roman rule is aggravated by bitter rivalry amongst the natives. The challenges that face two old friends and their comrades in arms are unlike any they have seen before - in a land that declares itself unconquerable.." });
            }
